// style.css
String style_css = R"x(
* {
    padding: 0;
    margin: 0;
}

body {
    width: 100vw;
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 16px;
}

body::before {
    content: "";
    position: fixed;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    opacity: 0.3;
    z-index: 0;
    background-image: url("data:image/jpeg;base64,");
    background-repeat: none;
    background-size: cover;
    background-position: 50% 50%;
}

.card {
    z-index: 1;
    background-color: rgba(0, 0, 0, .3);
    color: #fff;
    border: .25rem solid #fff;
    border-radius: 1rem;
    max-width: 90%;
    padding: 1rem;
    display: flex;
    flex-direction: column;
}

.card > * {
    margin: .75rem;
    border-radius: 2rem;
    font-size: 1.5rem;
}

input[type=text], .card > button {
    padding: .5rem 1.5rem;
    color: #fff;
    border: .125rem solid #fff;
}

input[type=text] {
    background-color: rgba(0,0,0, 0.5);
}

.card > button {
    background-color: rgba(255,255,255, 0.5);
}

input[type=text]:focus, .card > button:focus {
    outline: .25rem solid #fff;
    outline-offset: -2px;
}

input[type=text]::placeholder {
    color: #aaa;
    text-align: right;
}

.input-control {
    position: relative;
}

.tip {
    background-color: transparent;
    color: #fff;
    text-align: center;
}

.tip.error {
    color: yellow;
}
)x";

